	//视频分享
	public function index($id=0,$zu=0,$ji=0){
		$id = (int)$id;
		$zu = (int)$zu;
		$ji = (int)$ji;
		if($id==0) exit('视频ID错误');
		$row = $this->csdb->get_row('vod','*',array('id'=>$id));
		if(!$row) exit('视频不存在');
		//组集数
		$arr = $zuarr = array();
		if(!empty($row->url)) $arr = explode("#ctcms#",$row->url);
        for($i=0;$i<count($arr);$i++){
			$arr2 = explode("###",$arr[$i]);
			$arr3 = explode("\n",$arr2[1]);
			$jiarr=array();
			for($k=0;$k<count($arr3);$k++){
			    if(!empty($arr3[$k])){
					$arr4 = explode("$",$arr3[$k]);
					$arr5 = explode('&type=',trim($arr4[1]));
					$jiarr[$k]['purl'] = $arr5[0];
			    }
			}
			$zuarr[$i]['ji']=$jiarr;
        }
        $row->purl = $zuarr[$zu]['ji'][$ji]['purl'];
		//图片
		$row->pic = getpic($row->pic);
		$uid = $this->input->get('uid');
		$vod = $this->csdb->get_select('vod','id,name,pic,hits,text',array('cid'=>$row->cid),'rhits DESC',6);

		//判断收费
		if($row->vip > 0){
			$player = '<div class="video-player__error"><div class="inner"><p>该视频为收费视频，请下载APP观看</p><div class="btns"><a href="'.Web_Path.'index.php/app/share/down" class="app__download">下载App观看</a></div></div></div>';
		}else{
			$player = '<div class="vedio"><video style="width:100%;height: 230px;background:#000;" class="dplayer-video dplayer-video-current" webkit-playsinline="" playsinline="" poster="'.$row->pic.'" preload="auto" src="'.$row->purl.'" controls="controls"></video></div>';
		}

