pan></h1></div></div><div style="height: .5rem; background: #e8e7e8;"></div><div class="video-list like-list"><div class="video-list__header"><h2>猜你喜欢</h2></div><div style="border-bottom: 1px solid #f1f1f1; margin: 1rem -1rem;"></div><div class="video-list__content">';
		foreach($vod as $row2){
			echo '<div class="video-list__item"><a href="'.Web_Path.'index.php/app/share/index/'.$row2->id.'?uid='.$uid.'" class="cover"><img src="'.$row2->pic.'"/></a><div class="main"><h2><a href=""class="btn">'.$row2->name.'</a></h2><a href=""title="简介"class="intro btn"><p>'.sub_str($row2->text,50).'</p></a><div class="footer"><p class="play-count">播放:'.$row2->hits.'</p><div class="app--btn-xs"><a href="'.Web_Path.'index.php/app/share/down" class="app__download">App播放</a></div></div></div></div>';
		}
		echo '</div></div></div></div></body></html>';
	}

